mapper.update:
update sys_job
job_name = #{jobName}, job_group = #{jobGroup}, method_name = #{methodName}, method_params = #{methodParams}, cron_expression = #{cronExpression}, misfire_policy = #{misfirePolicy}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where job_id = #{jobId}

UPDATE emp_attend_calendar
SET effect_flag = 0
WHERE calendar_id = #{calendar_id}

