mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}

update sys_user
emp_id = #{empId}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
		select count(1) from sys_user su where su.del_flag = 0
		and su.dept_id = #{deptId}
		and su.user_id in 
		
            #{userId}
